import { serve } from "https://deno.land/std@0.168.0/http/server.ts";
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    // Create Supabase admin client
    const supabaseAdmin = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_SERVICE_ROLE_KEY') ?? ''
    );

    console.log('Setting up avatar storage bucket and policies');

    // Create the avatars bucket if it doesn't exist
    const { data: buckets, error: listError } = await supabaseAdmin.storage.listBuckets();

    if (listError) {
      console.error('Error listing buckets:', listError);
      throw listError;
    }

    const avatarBucketExists = buckets?.some(bucket => bucket.name === 'avatars');

    if (!avatarBucketExists) {
      console.log('Creating avatars bucket');
      const { data: bucket, error: createError } = await supabaseAdmin.storage.createBucket('avatars', {
        public: true,
        allowedMimeTypes: ['image/jpeg', 'image/png', 'image/webp'],
        fileSizeLimit: 5242880, // 5MB
      });

      if (createError) {
        console.error('Error creating bucket:', createError);
        throw createError;
      }

      console.log('Avatars bucket created successfully:', bucket);
    } else {
      console.log('Avatars bucket already exists');
    }

    // Set up RLS policies using SQL
    const policies = [
      {
        name: 'Avatar images are publicly accessible',
        sql: `
          CREATE POLICY IF NOT EXISTS "Avatar images are publicly accessible" ON storage.objects
          FOR SELECT USING (bucket_id = 'avatars');
        `
      },
      {
        name: 'Users can upload their own avatar',
        sql: `
          CREATE POLICY IF NOT EXISTS "Users can upload their own avatar" ON storage.objects
          FOR INSERT WITH CHECK (bucket_id = 'avatars' AND auth.uid()::text = (storage.foldername(name))[1]);
        `
      },
      {
        name: 'Users can update their own avatar',
        sql: `
          CREATE POLICY IF NOT EXISTS "Users can update their own avatar" ON storage.objects
          FOR UPDATE USING (bucket_id = 'avatars' AND auth.uid()::text = (storage.foldername(name))[1]);
        `
      },
      {
        name: 'Users can delete their own avatar',
        sql: `
          CREATE POLICY IF NOT EXISTS "Users can delete their own avatar" ON storage.objects
          FOR DELETE USING (bucket_id = 'avatars' AND auth.uid()::text = (storage.foldername(name))[1]);
        `
      }
    ];

    for (const policy of policies) {
      console.log(`Creating policy: ${policy.name}`);
      const { error: policyError } = await supabaseAdmin.rpc('exec_sql', {
        sql: policy.sql
      });

      if (policyError) {
        console.error(`Error creating policy ${policy.name}:`, policyError);
        // Don't throw here, continue with other policies
      } else {
        console.log(`Policy ${policy.name} created successfully`);
      }
    }

    return new Response(
      JSON.stringify({ 
        success: true, 
        message: 'Avatar storage setup completed successfully' 
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 200,
      }
    );

  } catch (error) {
    console.error('Error in setup-avatar-storage function:', error);
    return new Response(
      JSON.stringify({ 
        success: false, 
        error: error.message 
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 500,
      }
    );
  }
});

